def parse_sleep_time(sleep):
  if sleep.endswith('s'):
    return int(sleep.strip('s').strip())
  elif sleep.endswith('m'):
    return int(sleep.strip('m').strip()) * 60
  elif sleep.endswith('h'):
    return int(sleep.strip('h').strip()) * 60 * 60

